mapper.select:
select
  id, type, emp_id, license_number, rent, master_card, condition
from car


        and id = #{id}


        and type = #{type}


        and emp_id = #{empId}


        and license_number = #{licenseNumber}


        and rent = #{rent}


        and master_card = #{masterCard}


        and condition = #{condition}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from car


        and id = #{id}


        and type = #{type}


        and emp_id = #{empId}


        and license_number = #{licenseNumber}


        and rent = #{rent}


        and master_card = #{masterCard}


        and condition = #{condition}
