mapper.select:
select
  id, name, phone, card_id, gender, car_number
from customer


        and id = #{id}


        and name = #{name}


        and phone = #{phone}


        and card_id = #{cardId}


        and gender = #{gender}


        and car_number = #{carNumber}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from customer


        and id = #{id}


        and name = #{name}


        and phone = #{phone}


        and card_id = #{cardId}


        and gender = #{gender}


        and car_number = #{carNumber}
